package com.nut.cdev.fibonacci.ext

import org.junit.Assert.*
import org.junit.Before

import org.junit.Test
import java.math.BigInteger

class FibonacciExtKtTest {
    @Test
    fun testGenerateFibonacci_10_values() {
        val expected = mutableListOf<BigInteger>(
            BigInteger.ZERO,
            BigInteger.ONE,
            BigInteger.ONE,
            BigInteger.TWO,
            BigInteger.valueOf(3),
            BigInteger.valueOf(5),
            BigInteger.valueOf(8),
            BigInteger.valueOf(13),
            BigInteger.valueOf(21),
            BigInteger.valueOf(34)
        )
        assertEquals(expected, generateFibonacciList(10))

    }

    @Test
    fun test_GenerateFibonacci_at_500_TH() {
        val expected =
            "139423224561697880139724382870407283950070256587697307264108962948325571622863290691557658876222521294125"
        assertEquals(expected, generateFibonacciAt(500).toString())

    }

    @Test
    fun test_GenerateFibonacci_at_5000_TH() {
        val expected =
            ""
        assertEquals(expected, generateFibonacciAt(5000).toString())

    }
}